        public static void Printmap(int x, int y,ConsoleColor textcolor = ConsoleColor.White)
        {
            Console.SetCursorPosition(80, 27);
            char[,] map = new char[,]
            {
                {'□','□','□','□','□' },
                {'□','□','□','□','□' },
                {'□','□','□','□','□' },
                {'□','□','□','□','□' },
                {'□','□','□','□','□' }
            };
            for (int i = 0; i < map.GetLength(0); i++)
            {
                for (int j = 0; j < map.GetLength(1); j++)
                {
                    if (i == x && j == y)
                    {
                        Console.ForegroundColor = textcolor;
                        Console.Write("■");
                        Console.ResetColor();
                    }
                    else
                    {
                        Console.Write(map[i, j]);
                    }
                }
                Console.WriteLine();
                Console.SetCursorPosition(80, 27 + i);

            }
            


        }

        
